changeSet.sql:
CREATE TRIGGER trigger_documents_updated_at
BEFORE UPDATE ON execution.documents
FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

-- Add new column with enum type ALTER TABLE execution.documents ADD COLUMN status document_status DEFAULT 'DRAFT';
-- Migrate existing data if any (map VARCHAR values to enum) UPDATE execution.documents SET status = CASE WHEN UPPER(signer_status) = 'DRAFT' THEN 'DRAFT'::document_status WHEN UPPER(signer_status) = 'PENDING' THEN 'PENDING'::document_status WHEN UPPER(signer_status) = 'IN_PROGRESS' THEN 'IN_PROGRESS'::document_status WHEN UPPER(signer_status) = 'COMPLETED' THEN 'COMPLETED'::document_status WHEN UPPER(signer_status) = 'DECLINED' THEN 'DECLINED'::document_status WHEN UPPER(signer_status) = 'VOIDED' THEN 'VOIDED'::document_status WHEN UPPER(signer_status) = 'EXPIRED' THEN 'EXPIRED'::document_status WHEN UPPER(signer_status) = 'ERROR' THEN 'ERROR'::document_status ELSE 'DRAFT'::document_status END WHERE signer_status IS NOT NULL;
-- Drop old column and rename new one ALTER TABLE execution.documents DROP COLUMN signer_status;
-- Create index on the new status column CREATE INDEX idx_documents_status ON execution.documents(status);

changeSet.rollback:
ALTER TABLE execution.documents ADD COLUMN signer_status VARCHAR(50);
UPDATE execution.documents SET signer_status = status::text;
DROP INDEX IF EXISTS execution.idx_documents_status;
ALTER TABLE execution.documents DROP COLUMN status;

changeSet.sql:
ALTER TABLE execution.documents
